import React, {Component} from 'react';
import { connect } from 'react-redux'
import InputRange from 'react-input-range';
import 'react-input-range/lib/css/index.css';
import { SlideToggle } from 'react-slide-toggle';
import {Link} from 'react-router-dom';

import {getBrands, getColors, getMinMaxPrice} from '../../../services';
import {filterBrand, filterColor, filterPrice, filterSearch} from '../../../actions'

class Filter extends Component {

    constructor(props) {
        super(props);

        this.state = {
            openFilter: false
        }
    }

    closeFilter = () => {
        document.querySelector(".collection-filter").style = "left: -365px";
    }
    componentWillMount(){
        
        var categories = [];
        categories.push(this.props.category);
       
       // this.props.filterBrand(categories);

    }
    componentDidUpdate(prevProps, prevState){
        if(prevProps.category !== this.props.category){
            var categories = [];
            categories.push(this.props.category);
            this.props.filterBrand(categories);
        }
    }
    clickBrandHendle(event, brands) {

        var index = brands.indexOf(event.target.value);
        if (event.target.checked)
            brands.push(event.target.value); // push in array checked value
        else
            brands.splice(index, 1); // removed in array unchecked value



        this.props.filterBrand(brands);
    }

    colorHandle(event, color){
        var elems = document.querySelectorAll(".color-selector ul li");
        [].forEach.call(elems, function(el) {
            el.classList.remove("active");
        });
        event.target.classList.add('active');
        this.props.filterColor(color)
    }

    render (){
        const filteredBrands = this.props.filters.brand;
        //console.log(this.props.brands);
        return (
                <div class="collection-filter-block" style={{border:'0px'}}>
                    <div>
                        <form class="form_search" role="form" style={{ marginLeft:' -10%', height:'35px',width:'300px',borderRadius:'100px',border:'1px #ddddddab solid' }}>
                            <input style={{height:'32px'}} id="query search-autocomplete" type="search" class="nav-search nav-search-field" aria-expanded="true" placeholder="Type And Hit Enter"  onChange={e => this.props.filterSearch(e.target.value)}/>
                            <button type="submit" name="nav-submit-button" class="btn-search" style={{
                                                                                                            top: '5.5px',
                                                                                                            right: '10px',
                                                                                                            width:'10%',
                                                                                                            fontSize:'15px'
                                                                                                        }}>
                                                    <i class="fa fa-search" style={{
                                                                                        fontSize: '15px',
                                                                                        color:'#443d3d6e'
                                                                                        }}></i>
                            </button>
                        </form>
                    </div>

                    {/*brand filter start*/}
                    <div class="collection-mobile-back">
                        <span class="filter-back" onClick={(e) => this.closeFilter(e)} >
                            <i class="fa fa-angle-left" aria-hidden="true"></i> back
                        </span>
                    </div>
                    <SlideToggle>
                        {({onToggle, setCollapsibleElement}) => (
                            <div class="collection-collapse-block">
                                <h3 class="collapse-block-title" style={{letterSpacing: '3px',fontSize: '13px'}} onClick={onToggle}>CATEGORIES</h3>
                                <div class="collection-collapse-block-content"  ref={setCollapsibleElement}>
                                    <div class="collection-brand-filter">
                                        {this.props.brands.map((brand, index) => {
                                            return (
                                                <div class="custom-control custom-checkbox collection-filter-checkbox" style={{    borderBottom: '1px #dddddd5c solid',
                                                    paddingBottom: '5px'
                                                }} key={index}>
                                                    {/* <input type="checkbox" onClick={(e) => this.clickBrandHendle(e,filteredBrands)} value={brand} defaultChecked={filteredBrands.includes(brand)? true : false}  class="custom-control-input" id={brand} /> */}
                                                   
                                                    <Link  class="general-menu " to={`/Category/${brand}`} > 
                                                    <i class="fa fa-long-arrow-right mx-2"      style={{
                                                    fontSize: '11px',
                                                    fontWeight: '300'
                                                }} aria-hidden="true"></i>
                                                        {brand}
                                                    </Link>
                                                </div> )
                                        })}
                                    </div>
                                </div>
                            </div>
                        )}
                    </SlideToggle>

                    {/*color filter start here*/}
                    {/* <SlideToggle>
                        {({onToggle, setCollapsibleElement}) => (
                            <div class="collection-collapse-block">
                                <h3 class="collapse-block-title" onClick={onToggle}>colors</h3>
                                <div class="collection-collapse-block-content" ref={setCollapsibleElement}>
                                    <div class="color-selector">
                                        <ul>
                                            {this.props.colors.map((color, index) => {
                                                return (
                                                    <li class={color} title={color} onClick={(e) => this.colorHandle(e, color)} key={index}></li> )
                                            })}
                                        </ul>
                                    </div>
                                </div>
                            </div>
                        )}
                    </SlideToggle> */}
                    {/*price filter start here */}
                    {/* <SlideToggle>
                        {({onToggle, setCollapsibleElement}) => (
                            <div class="collection-collapse-block open">
                                <h3 class="collapse-block-title"  style={{letterSpacing: '3px',fontSize: '13px'}}  onClick={onToggle}>Filter By price</h3>
                                <div class="collection-collapse-block-content block-price-content" ref={setCollapsibleElement}>
                                    <div class="collection-brand-filter">
                                        <div class="custom-control custom-checkbox collection-filter-checkbox">
                                            <InputRange
                                                maxValue={this.props.prices.max}
                                                minValue={this.props.prices.min}
                                                value={this.props.filters.value}
                                                onChange={value => this.props.filterPrice({ value })} />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}
                    </SlideToggle> */}
                </div>
        )
    }
}


const mapStateToProps = (state, ownProps) => ({
    // category:ownProps.props.category,
    brands: getBrands(state.data.products),
    colors: getColors(state.data.products),
    prices: getMinMaxPrice(state.data.products,state.filters.country),

    filters: state.filters
})

export default connect(
    mapStateToProps,
    { filterBrand, filterColor, filterPrice, filterSearch }
)(Filter);
